import React, { useEffect, useState } from "react"
import { Token } from "../Main"
import { useEthers, useTokenBalance, useNotifications } from "@usedapp/core"
import { formatUnits } from "@ethersproject/units"
import { Button, CircularProgress, Input, Snackbar } from "@material-ui/core"
import { useStakeTokens } from "../../hooks"
import { utils } from "ethers"
import { Alert } from '@material-ui/lab'



interface StakeFormProps {
    token: Token
}

export const StakeForm = ({ token }: StakeFormProps) => {
    const { address: tokenAddress, name } = token
    const { account } = useEthers()
    const tokenBalance = useTokenBalance(tokenAddress, account)
    const formattedTokenBalance: number = tokenBalance ? parseFloat(formatUnits(tokenBalance, 18)) : 0
    const { notifications } = useNotifications()

    const [amount, setAmount] = useState<number | string | Array<number | string>>(0)
    const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        const newAmount = event.target.value === "" ? "" : Number(event.target.value)
        setAmount(newAmount)
        console.log(newAmount)
    }

    const { approveAndStake, state: approveAndStakeErc20State } = useStakeTokens(tokenAddress)
    const handleStakeSubmit = () => {
        const amountAsWei = utils.parseEther(amount.toString())
        return approveAndStake(amountAsWei.toString())
    }

    const isMining = approveAndStakeErc20State.status === "Mining"

    useEffect(() => {
        if (notifications.filter((notifications) => notifications.type === "transactionSucceed" && notifications.transactionName === "Approve ERC20 transfer").length > 0) {
            console.log("Approved!")
        }
        if (notifications.filter((notifications) => notifications.type === "transactionSucceed" && notifications.transactionName === "Stake Tokens").length > 0) { console.log("Tokens Staked") }
    }, [notifications])

    return (
        <>
            <div>
                <Input onChange={handleInputChange}></Input>
                <Button
                    onClick={handleStakeSubmit}
                    color="primary"
                    size="large"
                    disabled={isMining}>
                    {isMining ? <CircularProgress size={26} /> : "Stake!!!"}
                </Button>
            </div>
        </>
    )
}
